import { motion } from "framer-motion"
export default function GhostBtn(props) {
  return (
    <a rel="noopener noreferrer" target="_blank" href={props.url}>
      {" "}
      <motion.button
        animate={{
          y: [50, 0],
          opacity: [0, 1],
        }}
        transition={{ duration: 0.4 }}
        id="ghost"
        className="px-8 py-4 text-2xl text-green-200 bg-white dark:bg-black rounded-md font-head"
      >
        {props.text}
        <style jsx>{`
          #ghost {
            box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06);
          }
          @media (prefers-color-scheme: dark) {
            #ghost {
              box-shadow: 0 4px 6px -1px rgba(255, 255, 255, 0.1), 0 2px 4px -1px rgba(255, 255, 255, 0.6);
            }
          }
        `}</style>
      </motion.button>
    </a>
  )
}
