  const [ref, inView] = useInView();

  useEffect(() => {
    if (inView) {
      constrols.start("visible");
    }
  }, [controls, inView]);

  return (
    <a href={props.url}>
      <button className="px-8 py-4 text-2xl text-black bg-green-200 shadow-md dark:text-white hover:text-white dark:hover:text-black font-head rounded-md">
        {props.text}
      </button>
    </a>
  )
}
